// client/src/components/AccountPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode'; // Update the import statement
import '../styles/tailwind.css'; // Import the stylesheet


function AccountPage() {
  const [userDetails, setUserDetails] = useState({});

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const decoded = jwtDecode(token);
          const userId = decoded.id; // Assuming 'id' is the key for user ID in your JWT
          const response = await axios.get(`http://localhost:5000/api/auth/user/${userId}`);
          setUserDetails(response.data);
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    fetchUserDetails();
  }, []);

  return (
    <div className="main container mx-auto mt-8 p-8 bg-white shadow-lg rounded-lg">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">User Profile</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="flex items-center">
          <span className="font-medium text-gray-700 mr-4">Username:</span>
          <span className="text-gray-600">{userDetails.username}</span>
        </div>

        <div className="flex items-center">
          <span className="font-medium text-gray-700 mr-4">Phone:</span>
          <span className="text-gray-600">{userDetails.phone}</span>
        </div>

        <div className="flex items-center">
          <span className="font-medium text-gray-700 mr-4">Email:</span>
          <span className="text-gray-600">{userDetails.email}</span>
        </div>

        <div className="flex items-center">
          <span className="font-medium text-gray-700 mr-4">Address:</span>
          <span className="text-gray-600">{userDetails.address}</span>
        </div>
      </div>

      {/* Add more sections or details as needed */}
      {/* Example: Profile Picture, Edit Profile Button */}
    </div>
  );
}

export default AccountPage;
