// src/components/OrderConfirmPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function OrderConfirmPage() {
  const [cart, setCart] = useState(null);
  const [address, setAddress] = useState('');
  const [couponCode, setCouponCode] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/cart', {
          headers: {
            'x-auth-token': token,
          },
        });
        setCart(response.data);
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };

    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/protected', {
          headers: {
            'x-auth-token': token,
          },
        });
        setAddress(response.data.address);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    fetchCart();
    fetchUser();
  }, []);

  const handlePlaceOrder = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/orders/place', {
        address,
        couponCode,
      }, {
        headers: {
          'x-auth-token': token,
        },
      });
      setMessage('Order placed successfully');
      setTimeout(() => navigate('/orders'), 2000);
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  if (!cart) {
    return <div>Loading...</div>;
  }

  const totalPrice = cart.items.reduce((sum, item) => sum + item.itemId.price * item.quantity, 0);

  return (
    <div>
      <h1>Order Confirmation</h1>
      {cart.items.length === 0 ? (
        <p>Your cart is empty!</p>
      ) : (
        <div>
          {cart.items.map(({ itemId, quantity }) => (
            <div key={itemId._id}>
              <h2>{itemId.name}</h2>
              <p>Price: ${itemId.price.toFixed(2)}</p>
              <p>Quantity: {quantity}</p>
            </div>
          ))}
          <h3>Total Price: ${totalPrice.toFixed(2)}</h3>
          <div>
            <label>Address:</label>
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div>
            <label>Coupon Code:</label>
            <input
              type="text"
              value={couponCode}
              onChange={(e) => setCouponCode(e.target.value)}
            />
          </div>
          <button onClick={handlePlaceOrder}>Place Order</button>
          {message && <p>{message}</p>}
        </div>
      )}
    </div>
  );
}

export default OrderConfirmPage;
