// client/src/components/OrderConfirmPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function OrderConfirmPage() {
  const [cart, setCart] = useState(null);
  const [user, setUser] = useState(null);
  const [coupon, setCoupon] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);
  const [discountedPrice, setDiscountedPrice] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/cart', {
          headers: { 'x-auth-token': token },
        });
        setCart(response.data);
        calculateTotalPrice(response.data.items);
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };

    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { 'x-auth-token': token },
        });
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    fetchCart();
    fetchUser();
  }, []);

  const calculateTotalPrice = (items) => {
    const total = items.reduce((sum, item) => sum + item.itemId.price * item.quantity, 0);
    setTotalPrice(total);
    setDiscountedPrice(total); // Initially set discounted price to total price
  };

  const handleApplyCoupon = () => {
    if (coupon === 'DBMS50') {
      setDiscountedPrice(totalPrice * 0.5);
    } else {
      setDiscountedPrice(totalPrice);
    }
  };

  const handlePlaceOrder = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/orders',
        {
          items: cart.items,
          totalPrice: discountedPrice,
          address: user.address,
        },
        {
          headers: { 'x-auth-token': token },
        }
      );
      navigate('/order-confirmation'); // Navigate to order confirmation page after placing order
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  if (!cart || !user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Order Confirmation</h1>
      <h2>Delivery Address</h2>
      <p>{user.address}</p>
      <h2>Cart Items</h2>
      {cart.items.map(({ itemId, quantity }) => (
        <div key={itemId._id}>
          <h3>{itemId.name}</h3>
          <p>Price: Rs{itemId.price.toFixed(2)}</p>
          <p>Quantity: {quantity}</p>
        </div>
      ))}
      <h2>Total Price: Rs{totalPrice.toFixed(2)}</h2>
      <h2>Discounted Price: Rs{discountedPrice.toFixed(2)}</h2>
      <input
        type="text"
        value={coupon}
        onChange={(e) => setCoupon(e.target.value)}
        placeholder="Enter coupon code"
      />
      <button onClick={handleApplyCoupon}>Apply Coupon</button>
      <button onClick={handlePlaceOrder}>Place Order</button>
    </div>
  );
}

export default OrderConfirmPage;
