// client/src/components/AccountPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode'; // Update the import statement
import '../styles/tailwind.css'; // Import the stylesheet


function AccountPage() {
  const [userDetails, setUserDetails] = useState({});

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const decoded = jwtDecode(token);
          const userId = decoded.id; // Assuming 'id' is the key for user ID in your JWT
          const response = await axios.get(`http://localhost:5000/api/auth/user/${userId}`);
          setUserDetails(response.data);
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    fetchUserDetails();
  }, []);

  return (
    <div className="main container mx-auto mt-8 p-4 bg-white shadow-md rounded-md center"> 
      <h2 className="text-2xl font-semibold mb-4 text-center">User Profile</h2>

      <div className="grid grid-cols-2 gap-4"> 
        <div className="font-medium">Username:</div>
        <div>{userDetails.username}</div>

        <div className="font-medium">Phone:</div>
        <div>{userDetails.phone}</div>

        <div className="font-medium">Email:</div>
        <div>{userDetails.email}</div>

        <div className="font-medium">Address:</div>
        <div>{userDetails.address}</div>
      </div>
    </div>
  );
}

export default AccountPage;
