// src/components/ItemPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ItemPage() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [message, setMessage] = useState('');
  const [showQuantityInput, setShowQuantityInput] = useState(false);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/items/${id}`);
        setItem(response.data);
      } catch (error) {
        console.error('Error fetching item:', error);
      }
    };

    fetchItem();
  }, [id]);

  const handleAddToCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/cart/add', {
        itemId: id,
        quantity,
      }, {
        headers: {
          'x-auth-token': token,
        },
      });
      setMessage('Item added to cart');
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  const handleRemoveFromCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/cart/remove', {
        itemId: id,
      }, {
        headers: {
          'x-auth-token': token,
        },
      });
      setMessage('Item removed from cart');
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto mt-8 p-4 bg-white shadow-md rounded-md"> 
      <h1 className="text-3xl font-semibold mb-4">{item.name}</h1>
      <p className="text-xl text-gray-700 mb-2">Rs{item.price.toFixed(2)}</p>
      <img src={item.imageUrl} alt={item.name} className="mb-4 rounded-md" />
      <p className="mb-6">{item.description}</p>

      {showQuantityInput ? (
        <div className="flex items-center space-x-2 mb-4">
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            min="1"
            className="border rounded-md px-2 py-1 w-16 text-center"
          />
          <button
            onClick={handleAddToCart}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-md"
          >
            Add
          </button>
        </div>
      ) : (
        <button
          onClick={() => setShowQuantityInput(true)}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-md mb-4"
        >
          Add to Cart
        </button>
      )}

      <button
        onClick={handleRemoveFromCart}
        className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-md"
      >
        Remove from Cart
      </button>

      {message && <p className="mt-4">{message}</p>}
    </div>
  );
}

export default ItemPage;
