// src/components/PaymentPage.js

import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const PaymentPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [userAddress, setUserAddress] = useState(''); // Placeholder for user's address
  const [deliveryDate, setDeliveryDate] = useState('Tomorrow, at 11:00 AM');
  const [showPopup, setShowPopup] = useState(false);
  const history = useHistory();

  useEffect(() => {
    // Fetch cart items and user address from API or context
    // For now, using dummy data
    const fetchCartDetails = async () => {
      // Replace with your actual API call
      const cartResponse = await fetch('/api/cart');
      const cartData = await cartResponse.json();
      setCartItems(cartData.items);

      // Fetch user address
      // Replace with your actual API call
      const addressResponse = await fetch('/api/user/address');
      const addressData = await addressResponse.json();
      setUserAddress(addressData.address);
    };

    fetchCartDetails();
  }, []);

  const handleOrderNow = () => {
    setShowPopup(true);
    setTimeout(() => {
      setShowPopup(false);
      history.push('/orders'); // Redirect to OrdersPage
    }, 1000); // Duration of popup
  };

  return (
    <div>
      <h1>Order Summary</h1>
      <div>
        <h2>Items in Cart</h2>
        <ul>
          {cartItems.length > 0 ? (
            cartItems.map(item => (
              <li key={item.id}>
                {item.name} - {item.quantity} x {item.price} INR
              </li>
            ))
          ) : (
            <li>No items in cart</li>
          )}
        </ul>
      </div>
      <div>
        <h2>User Address</h2>
        <p>{userAddress || 'Address not available'}</p>
      </div>
      <div>
        <h2>Delivery Details</h2>
        <p>{deliveryDate}</p>
      </div>
      <button onClick={handleOrderNow}>Order Now</button>
      {showPopup && <div className="popup">Order Confirmed</div>}
    </div>
  );
};

export default PaymentPage;
