// src/components/PaymentPage.js

import React from 'react';

const PaymentPage = () => {
  const handlePayment = () => {
    fetch('/api/order', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ amount: 1000 }) })
      .then(res => res.json())
      .then(order => {
        const options = {
          key: 'your-public-key-from-razorpay',
          amount: order.amount,
          currency: order.currency,
          name: 'Your Company Name',
          order_id: order.id,
          handler: function (response) {
            fetch('/api/payment/verify', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ ...response }) })
              .then(res => res.json())
              .then(result => alert('Payment Successful!'))
              .catch(err => console.error('Payment verification failed:', err));
          },
          prefill: {
            name: 'Your Name',
            email: 'your-email@example.com',
            contact: '9999999999',
          },
          theme: {
            color: '#3399cc',
          },
        };
        const paymentObject = new window.Razorpay(options);
        paymentObject.open();
      })
      .catch(err => console.error('Order creation failed:', err));
  };

  return (
    <div>
      <h1>Payment Page</h1>
      <button onClick={handlePayment}>Pay Now</button>
    </div>
  );
};

export default PaymentPage;
