// src/components/ItemPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ItemPage() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [message, setMessage] = useState('');
  const [showQuantityInput, setShowQuantityInput] = useState(false);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/items/${id}`);
        setItem(response.data);
      } catch (error) {
        console.error('Error fetching item:', error);
      }
    };

    fetchItem();
  }, [id]);

  const handleAddToCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/cart/add', {
        itemId: id,
        quantity,
      }, {
        headers: {
          'x-auth-token': token,
        },
      });
      setMessage('Item added to cart');
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  const handleRemoveFromCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/cart/remove', {
        itemId: id,
      }, {
        headers: {
          'x-auth-token': token,
        },
      });
      setMessage('Item removed from cart');
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto mt-8 p-4 bg-white shadow-md rounded-md">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4"> 
        <div className="flex justify-center items-center">
          <img src={item.imageUrl} alt={item.name} className="w-full max-h-[400px] object-contain rounded-md" />
        </div>

        <div className="flex flex-col justify-center">
          <h1 className="text-3xl font-semibold mb-2">{item.name}</h1>
          <p className="text-xl text-gray-700 mb-4">Rs{item.price.toFixed(2)}</p>
          <p className="mb-6">{item.description}</p>

          {showQuantityInput ? (
            <div className="flex space-x-2">
              <input
                type="number"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                min="1"
                className="border rounded-md px-2 py-1 w-16 text-center"
              />
              <button 
                onClick={handleAddToCart}
                className="bg-brown-700 hover:bg-brown-600 text-beige-100 font-semibold py-2 px-4 rounded-md" 
              >
                Add Â  

              </button>
            </div>
          ) : (
            <button
              onClick={() => setShowQuantityInput(true)}
              className="bg-brown-700 hover:bg-brown-600 text-beige-100 font-semibold py-2 px-4 rounded-md"
            >
              Add to Cart
            </button>
          )}

          <button
            onClick={handleRemoveFromCart}
            className="bg-brown-700 hover:bg-brown-600 text-beige-100 font-semibold py-2 px-4 rounded-md"
          >
            Remove from Cart
          </button>

          {message && <p className="mt-2">{message}</p>}
        </div>
      </div>
    </div>
  );
}

export default ItemPage;
